import React, { Fragment, useEffect, useState } from "react";

import EditTodo from "./EditTodo";
const ListTodos = () => {

    const [todos, setTodos] = useState([]);
    
    const deleteTodo = async (id) => {
        try {
            
            const deleteTodo = await fetch(`http://66.215.171.251:5000/todos/${id}`,{
                method: "DELETE"
            });
            
            setTodos(todos.filter(todo => todo.id !== id)); //no refresh needed
            console.log(deleteTodo);
            //window.location = "/";  // refresh*
        } catch (error) {
            console.error(error.message);
        }
    }

    const getTodos = async () => {
        try {
            
            const response = await fetch("http://66.215.171.251:5000/todos")
            const jsonData = await response.json();

            setTodos(jsonData);

        } catch (error) {
            console.error(error.message);
        }
    }

    useEffect(() => {
        getTodos();
    }, [])

    //console.log(todos);
    return <Fragment>
        <table class="table table-dark table-striped mt-5 text-center">
    <thead>
      <tr>
        {/* <th>ID</th> */}
        <th>Description</th>
        <th>Edit</th>
        <th>Delete</th>
      </tr>
    </thead>
    <tbody>
      {
        todos.map(
            todo => (
                <tr key= {todo.id}>
                    {/* <td>{todo.id}</td> */}
                    <td>{todo.description}</td>
                    <td><EditTodo eTodo = {todo}/></td>
                    <td><button className="btn btn-danger" onClick={() => deleteTodo(todo.id)}>DELETE</button></td>
                </tr>
            )
        )
      }
    </tbody>
  </table>
    </Fragment>;
}
//export { todos, setTodos };
export default ListTodos;
